// TODO: create a component that displays a single bakery item
import "./BakeryItem.css";

import React from 'react';

const BakeryItemMenu = ({ image, title, description, price, addToCart }) => {
  return (
    <div style={{ width: 211, height: 350, border: '2px solid #000000', margin: '10px', padding: '10px' }}>
      <div style={{ width: '100%', height: '70%', marginBottom: '10px' }}>
        <img src={image} alt={title} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
      </div>
      <div style={{ textAlign: 'center' }}>
        <h3>{name}</h3>
        <p>{description}</p>
        <p>Price: ${price}</p>
        <button onClick={addToCart}>Add to Cart</button>
      </div>
    </div>
  );
};

export default BakeryItemMenu;
