import React, { useState } from 'react';
import BakeryItemMenu from './BakeryItemMenu';

const ParentComponent = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (item) => {
    const existingItem = cart.find(cartItem => cartItem.name === item.name);

    if (existingItem) {
      // Create a new cart array with the updated item
      const newCart = cart.map(cartItem =>
        cartItem.name === item.name ? { ...cartItem, quantity: cartItem.quantity + 1 } : cartItem
      );
      setCart(newCart);
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
  };

  // Render your BakeryItemMenu components here, passing addToCart as a prop
  return (
    <div>
      {/* Render your BakeryItemMenu components here */}
      <BakeryItemMenu 
        image={item.image} 
        name={item.name} 
        description={item.description} 
        price={item.price} 
        addToCart={() => addToCart(item)} 
      />
      {/* More BakeryItemMenu components as needed */}
    </div>
  );
};
