// TODO: create a component that displays a single bakery item
import "./BakeryItem.css";

import React from 'react';

const BakeryItem = ({ image, title, description, price, addToCart }) => {
  return (
    <div className="bakery-item">
      <img src={image} alt={title} className="bakery-item-image" />
      <div className="bakery-item-details">
        <h2 className="bakery-item-title">{title}</h2>
        <p className="bakery-item-description">{description}</p>
        <p className="bakery-item-price">${price}</p>
        <button className="add-to-cart-button" onClick={addToCart}>
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default BakeryItem;
