import "./App.css";
import { useState } from "react";
import bakeryData from "./assets/bakery-data.json";
import BakeryItemMenu from "./components/BakeryItem"; // Import BakeryItemMenu component

/* ####### DO NOT TOUCH -- this makes the image URLs work ####### */
bakeryData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});
/* ############################################################## */

function App() {
  // Use useState to create a state variable to hold the state of the cart
  const [cart, setCart] = useState([]);

  // Function to add item to cart
  const addToCartHandler = (item) => {
    setCart([...cart, item]);
  };

  return (
    <div className="App">
      <h1>Sarah's Bakery</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {bakeryData.map((item, index) => (
          <BakeryItemMenu
            key={index}
            image={item.image}
            name={item.name}
            description={item.description}
            price={item.price}
            addToCart={() => addToCartHandler(item)}
          />
        ))}
      </div>

      <div>
        <h2>Cart</h2>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>{item.title} ${item.price}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
