import "./App.css";
import { useState } from "react";
import bakeryData from "./assets/bakery-data.json";
import BakeryItemMenu from "./components/BakeryItemMenu";

/* ####### DO NOT TOUCH -- this makes the image URLs work ####### */
bakeryData.forEach((item) => {
  item.image = process.env.PUBLIC_URL + "/" + item.image;
});
/* ############################################################## */

function App() {
  const [cart, setCart] = useState([]);

  const addToCartHandler = (item) => {
    setCart(current => [...current, item]);
  };

  return (
    <div className="App">
      <h1>Sarah's Bakery</h1>
      <div className="bakery-grid">
        {bakeryData.slice(0, 8).map((item, index) => ( // Adjust slice to control the total number of items (for 2 rows, with 4 items each)
          <BakeryItemMenu
            key={index}
            image={item.image}
            name={item.title}
            description={item.description}
            price={item.price}
            addToCart={() => addToCartHandler(item)}
          />
        ))}
      </div>

      <div>
        <h2>Cart</h2>
        <ul>
          {cart.map((item, index) => (
            <li key={index}>{item.title} - ${item.price}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
